import React from 'react';
import './hello.css';

let curhrs = new Date();
curhrs=  curhrs.getHours();
let curyear =new Date();
curyear =curyear.getFullYear();
let grea = ' ' ;
const cssStyle ={ };
if(curhrs>=0 && curhrs<12){
  grea='Good Morning';
  cssStyle.color='yellow';
}
else if(curhrs>=12 && curhrs<19){
  grea='Good Afternoon';
  cssStyle.color='blue';
}
else if(curhrs>=19 && curhrs<24){
  grea='Good Evening';
  cssStyle.color='white';
}
const Hello = () => {

  return (
    <>
      <div className="container">
        <div className="child">
          <h1>Hello sir,<span style={cssStyle}>{grea}</span> in the year {curyear}</h1>
        </div>
      </div>
    </>
  )
}

export default Hello;
