import React from 'react'
import { useInView } from 'react-intersection-observer'
import { motion, useAnimation } from 'framer-motion'
import { useEffect } from 'react'
import Util from './components/Util'

const AnimatedProgressBar = ({ progress }) => {
 
  const { ref, inView } = useInView()
  const controls = useAnimation()
  
  useEffect(() => {
    if (inView) {
      controls.start({
        width: `${progress}%`,
        transition: {
          duration: 1
        }
      })
    }
  }, [controls, inView, progress])
  
  return (
    <div style={{ width: '100%', height: 20, backgroundColor: '#f2f2f2', margin: "20px",  }}>
      <motion.div
        ref={ref}
        animate={controls}
        initial={{ width: 0 }}
        style={{
          width: 0,
          height: '100%',
          backgroundColor: 'blue',
          borderRadius: "8px",
          display: 'flex',
          justifyContent: 'end',
        }}>

        <span style={{color: "white",}}> {progress}% </span>

      </motion.div>
      
    </div>
  )

} 

function App() {

  const [skills, setSkills] = React.useState([60, 30, 90, 80, 50])

  return (
    <>
      <div>
        {
          skills.map((skill, index) => (
            <AnimatedProgressBar key={index} progress={skill} />
          ))
        }
      </div>
    </>
  )
}

export default App
