import React, { useEffect } from "react";
import Csskill from "./csskills";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";

const AnimatedProgressBar = (props) => {
  const { ref, inview } = useInView()
  const controls = useAnimation()

  useEffect(() => {
    if (inview) {
      controls.start({
        width: `${props.progress}%`,
        transition: {
          duration: 1
        }
      });
    }
  }, [controls, inview, props.progress]);
//   const myStyle = {
//     backgroundColor: cueskill.colo,
//     width: cueskill.perce,
//   };

  return (
    <>
      <h2 className="csshillh3">{props.skillname}</h2>
      <div className="progress-bar">
        <motion.div
        ref={ref}
        animate={controls}
        initial={{width:0}}
        style={{
            width:0,
            height:'100%',
            backgroundColor:"blue",
            display:'flex',
            justifyContent:"end",

        }}
        >
          <span>{props.progress}%</span>


          </motion.div>
        </div>
        
     
    </>
  );
};

// export fuction
const MotionAnime = () => {
  function csSkills(cueskill) {
    
    return <>
        <AnimatedProgressBar progress={cueskill.perce} skillname={cueskill.name} colo={cueskill.colo}/>
    </>;
  }

  return (
    <div>
      <div className="skills_cs">{Csskill.map(csSkills)}</div>
    </div>
  );
};

export default MotionAnime;
