import React, { useEffect } from "react";
import Csskill from "./csskills";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";

const AnimatedProgressBar = (props) => {
  const { ref, inview } = useInView();
  const control = useAnimation();

  useEffect(() => {
    if (inview) {
      control.start({
        width: `${props.progress}%`,
        transition: {
          duration: 1,
        },
      });
    }
  }, [control, inview, props.progress]);
//   const myStyle = {
//     backgroundColor: cueskill.colo,
//     width: cueskill.perce,
//   };

  return (
    <>
      <h2 className="csshillh3">{props.skillname}</h2>
      <div className="progress-bar">
        <div>
          <span>{props.progress}%</span>
        </div>
        
      </div>
    </>
  );
};

// export fuction
const MotionAnime = () => {
  function csSkills(cueskill) {
    
    return <>
        <AnimatedProgressBar progress={cueskill.perce} skillname={cueskill.name}/>
    </>;
  }

  return (
    <div>
      <div className="skills_cs">{Csskill.map(csSkills)}</div>
    </div>
  );
};

export default MotionAnime;
