import React, {useState} from "react";
import "./about.css";
import "./hero_btn.css";
const Aboutsection = () => {
    const [moseOver, setMouseOver] = useState(false);
    function herobtn_over() {
      setMouseOver(true);
    }
    function heobtn_leave() {
      setMouseOver(false);
    }
    const style = {
      backgroundColor: moseOver ? "crimson" : "black",
      color: moseOver ? "black" : "crimson",
      border: moseOver ? "black" : "white",
    };
    const [moreAbout, setAbout] = useState(false);

function aboutClicked(){
        setAbout(true);
    }

  return (
    <div className="about_sec">
      <div className="about_des_im">
        <img
          src="https://vrajinfosolution.com/wp-content/uploads/2023/04/development.png"
          alt="about image"
          className="about_img"
        />
      </div>
      <div className="about_des">
        <h1 className="about_des_h1">About Me</h1>
        <h2>Let me Introduce Myself</h2>
        <p>
      
        </p>

{/* 
        Along the way, I've honed my skills in front-end. I'm constantly seeking ways to innovate and elevate the digital experience.Outside of coding, you'll find me exploring the intersection of technology and design, experimenting with new frameworks, or immersing myself in the vibrant tech community. I thrive in collaborative environments and am always eager to learn from and share knowledge with fellow enthusiasts */}

        <button className="about_btn hero_btn" style={style} onMouseOver={herobtn_over}
        onMouseLeave={heobtn_leave}  onClick={aboutClicked}> Read More </button>

        {/*  */}
      </div>
    </div>
  );
};

export default Aboutsection;
