import React,{useState} from 'react'

const About_btn = () => {
    const [moseOver, setMouseOver] = useState(false);
    function herobtn_over() {
      setMouseOver(true);
    }
    function heobtn_leave() {
      setMouseOver(false);
    }
    const style = {
      backgroundColor: moseOver ? "crimson" : "black",
      color: moseOver ? "black" : "crimson",
      border: moseOver ? "black" : "white",
    };
    
    const [moreAbout, setAbout] = useState(false);

function aboutClicked(){
        setAbout(true);
    }
  return (
    <div>
    <show_more>
    Hey there! I'm Nitin Chakrawarti, a B.tech 1st year student and
          Frontend web Developer. I love turning ideas into functional websites.
          Passionate about creativity and problem-solving, I thrive on crafting
          seamless user experiencesMy journey in technology began with a
          curiosity-driven exploration of programming languages and software
          development. Along the way, I've honed my skills in front-end. I'm constantly seeking ways to innovate and elevate the digital experience.Outside of coding, you'll find me exploring the intersection of technology and design, experimenting with new frameworks, or immersing myself in the vibrant tech community. I thrive in collaborative environments and am always eager to learn from and share knowledge with fellow enthusiasts

    </show_more>
      <button className="about_btn hero_btn" style={style} onMouseOver={herobtn_over}
        onMouseLeave={heobtn_leave}  onClick={aboutClicked}> Read More </button>
    </div>
  )
}

export default About_btn
