import React, { useState } from "react";
import "./hero_btn.css";
import btnApi from "./hero_btnapi";

const Hero_button = () => {
  const [moseOver, setMouseOver] = useState(false);
  function herobtn_over() {
    setMouseOver(true);
  }
  function heobtn_leave() {
    setMouseOver(false);
  }
  const style = {
    backgroundColor: moseOver ? "crimson" : "black",
    color: moseOver ? "black" : "crimson",
    border: moseOver ? "black" : "white",
  };
  function btn_herosection(curEle) {
    return (
      <>
        <button
          style={style}
          className="hero_btn"
          onMouseOver={herobtn_over}
          onMouseLeave={heobtn_leave}
        >{curEle.cotent}</button>
      </>
    );
  }
  return <div className="hero-buttons">{btnApi.map(btn_herosection)}</div>;
};

export default Hero_button;
